spec.query:
import "date"



from(bucket: "smarthome")
    |> range(start: today())
    |> filter(fn: (r) => r._measurement == "mystromsolar" and r._field == "power")
    |> aggregateWindow(
        every: 1d,
        fn: (column, tables=<-) => tables |> integral(unit: 1h),
        createEmpty: false,
    )
    |> map(
        fn: (r) =>
            ({
                _time: date.add(d: 3h, to: today()),
                _measurement: "daily_energy",
                _field: "green_injected",
                _value: r._value,
            }),
    )
    |> to(bucket: "smarthomederived")
