spec.query:
import "date"



from(bucket: "smarthome")
    |> range(start: today())
    |> filter(fn: (r) => r._measurement == "tasmota" and r._field == "upstream")
    |> aggregateWindow(
        every: 1d,
        fn: (column, tables=<-) => tables |> integral(unit: 1h),
        createEmpty: false,
    )
    |> map(
        fn: (r) =>
            ({
                _time: date.add(to: today(), d: 3h),
                _measurement: "daily_energy",
                _field: "green_upstream",
                _value: r._value,
            }),
    )
    |> to(bucket: "smarthomederived")
